void test_ValidateDataLength() {
    TitaniumProtocol protocol;
    std::unique_ptr<TitaniumPackage> package = nullptr;

    uint8_t test_bytes_fail[] = {0x02, 0x04, 0x01, 'W', 0x01, 'L', 'U', 'C', 'A', 'S', 0x51, 0x6C, 0xA1, 0x25, 0x03};
    TEST_ASSERT_EQUAL(ProtocolErrors::INVALID_PAYLOAD_SIZE, protocol.Decode(test_bytes_fail, sizeof(test_bytes_fail), package));
}

void test_ValidateCommand() {
    TitaniumProtocol protocol;
    std::unique_ptr<TitaniumPackage> package = nullptr;

    uint8_t test_bytes_pass[] = {0x02, 0x00, 0x05, 'R', 0x01, 'L', 'U', 'C', 'A', 'S', 0xD8, 0x85, 0x9A, 0x5F, 0x03};
    TEST_ASSERT_EQUAL(ESP_OK, protocol.Decode(test_bytes_pass, sizeof(test_bytes_pass), package));
    uint8_t test_bytes_fail[] = {0x02, 0x00, 0x05, 'B', 0x01, 'L', 'U', 'C', 'A', 'S', 0xBF, 0x5B, 0x06, 0x91, 0x03};
    TEST_ASSERT_EQUAL(ProtocolErrors::INVALID_COMMAND, protocol.Decode(test_bytes_fail, sizeof(test_bytes_fail), package));
}

