void test_ValidateDataLength() {
    SerialProtocol protocol;

    uint8_t test_bytes_fail[] = {0x02, 0x04, 0x01, 'W', 0x01, 'L', 'U', 'C', 'A', 'S', 0x51, 0x6C, 0xA1, 0x25, 0x03};
    TEST_ASSERT_EQUAL(ESP_ERR_INVALID_SIZE, protocol.ProcessIncomingMessage(test_bytes_fail, sizeof(test_bytes_fail)));

}

void test_ValidateCommand() {
    SerialProtocol protocol;

    uint8_t test_bytes_pass[] = {0x02, 0x00, 0x05, 'R', 0x01, 'L', 'U', 'C', 'A', 'S', 0xD8, 0x85, 0x9A, 0x5F, 0x03};
    TEST_ASSERT_EQUAL(ESP_OK, protocol.ProcessIncomingMessage(test_bytes_pass, sizeof(test_bytes_pass)));
    uint8_t test_bytes_fail[] = {0x02, 0x00, 0x05, 'B', 0x01, 'L', 'U', 'C', 'A', 'S', 0xBF, 0x5B, 0x06, 0x91, 0x03};
    TEST_ASSERT_EQUAL(ESP_ERR_INVALID_ARG, protocol.ProcessIncomingMessage(test_bytes_fail, sizeof(test_bytes_fail)));

}

void test_ValidateCRC32() {
    SerialProtocol protocol;

    uint8_t test_bytes_fail[] = {0x02, 0x00, 0x05, 'R', 0x01, 'L', 'U', 'C', 'A', 'S', 0xD8, 0x85, 0x9A, 0x52, 0x03};
    TEST_ASSERT_EQUAL(ESP_ERR_INVALID_CRC, protocol.ProcessIncomingMessage(test_bytes_fail, sizeof(test_bytes_fail)));

}

void test_ValidateEndByte() {
    SerialProtocol protocol;

    uint8_t test_bytes_fail[] = {0x02, 0x00, 0x05, 'R', 0x01, 'L', 'U', 'C', 'A', 'S', 0xD8, 0x85, 0x9A, 0x5F, 0x01};
    TEST_ASSERT_EQUAL(ESP_FAIL, protocol.ProcessIncomingMessage(test_bytes_fail, sizeof(test_bytes_fail)));

}

void main_test(void) {
    vTaskDelay(pdMS_TO_TICKS(2000));

    UNITY_BEGIN();

    RUN_TEST(test_ValidateStartByte);
    RUN_TEST(test_ValidateDataLength);
    RUN_TEST(test_ValidateCommand);
    RUN_TEST(test_ValidateCRC32);
    RUN_TEST(test_ValidateEndByte);

    UNITY_END();
}

extern "C" void app_main(void) { main_test(); }